import Head from 'next/head'
import styles from '../styles/Home.module.css'
import CoinGecko from 'coingecko-api';
const coinGeckoClient = new CoinGecko();
import MaterialTable from 'material-table'

export default function Home(props) {
  const { data } = props.result;
  console.log("Show---->getAPI---->", data)
  const formatPercent = number =>
    `${new Number(number).toFixed(2)}%`

  const formatDollar = (number, maximumSignificantDigits) =>
    new Intl.NumberFormat(
      'en-US',
      {
        style: 'currency',
        currency: 'USD',
        maximumSignificantDigits
      })
      .format(number);

  return (
    <div className={styles.container}>
      <Head>
        <title>Coinmarketcap clone</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <h1>Coinmarketcap clone</h1>
      <table className="table">
        <thead>
          <tr>
            <th>Symbol</th>
            <th>24H Change</th>
            <th>Price</th>
            <th>Market cap</th>
          </tr>
        </thead>
        <tbody>
          {data.map(coin => (
            <tr key={coin.id}>
              <td>
                <img
                  src={coin.image}
                  style={{ width: 25, height: 25, marginRight: 10 }}
                />
                {coin.symbol.toUpperCase()}
              </td>
              <td>
                <span
                  className={coin.price_change_percentage_24h > 0 ? (
                    'text-success'
                  ) : 'text-danger'}
                >
                  {formatPercent(coin.price_change_percentage_24h)}
                </span>
              </td>
              <td>{formatDollar(coin.current_price, 20)}</td>
              <td>{formatDollar(coin.market_cap, 12)}</td>
            </tr>
          ))}

        </tbody>
      </table>
      <MaterialTable
        columns={[
          { title: "Symbol", field: 'image' },
          { title: "24HChange", field: 'price24' },
          { title: "Price", field: 'Price' },
          { title: "Market cap", field: 'Marketcap' },
        ]}
        data={data.map(coin => ([
          /*console.log("coin.image",coin.image),
          console.log("coin.price_change_percentage_24h",coin.price_change_percentage_24h),
          console.log("coin.current_price",coin.current_price),
          console.log("coin.market_cap",coin.market_cap),*/
          { image: coin.image, price24: coin.price_change_percentage_24h, Price: coin.current_price, Marketcap: coin.market_cap },
        ]))}
        title="Demo" />
    </div>
  )

}

export async function getServerSideProps(context) {
  const params = {
    order: CoinGecko.ORDER.MARKET_CAP_DESC
  };
  const result = await coinGeckoClient.coins.markets({ params });
  return {
    props: {
      result
    },
  }
}
